class n{constructor(){this.calculatorButtons=document.querySelectorAll(".calculator-item button"),this.init()}init(){this.setupEventListeners()}setupEventListeners(){this.calculatorButtons.forEach(t=>{t.addEventListener("click",this.handleCalculatorButtonClick.bind(this))})}handleCalculatorButtonClick(t){const e=t.target.id.replace("calculate-","");console.log(`Opening calculator: ${e}`)}}document.addEventListener("DOMContentLoaded",()=>{new n});
